import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Home, UserPlus, Users } from 'lucide-react';
import CreatePatientForm from './components/CreatePatientForm';
import PatientList from './components/PatientList';
import './App.css';

const Navigation: React.FC = () => {
  const location = useLocation();

  const isActive = (path: string) => location.pathname === path;

  return (
    <nav className="bg-slate-800 text-white shadow-lg">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center space-x-8">
            <h1 className="text-xl font-bold">üè• HBS - Hastane Bilgi Sistemi</h1>
            <div className="flex space-x-1">
              <Link
                to="/"
                className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
                  isActive('/') ? 'bg-slate-700' : 'hover:bg-slate-700'
                }`}
              >
                <Home size={18} />
                <span>Ana Sayfa</span>
              </Link>
              <Link
                to="/create-patient"
                className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
                  isActive('/create-patient') ? 'bg-slate-700' : 'hover:bg-slate-700'
                }`}
              >
                <UserPlus size={18} />
                <span>Yeni Hasta</span>
              </Link>
              <Link
                to="/patients"
                className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
                  isActive('/patients') ? 'bg-slate-700' : 'hover:bg-slate-700'
                }`}
              >
                <Users size={18} />
                <span>Hasta Listesi</span>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

const App: React.FC = () => {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50 flex flex-col">
        <Navigation />

        <main className="flex-1 container mx-auto px-4 py-8">
          <Routes>
            <Route path="/" element={
              <div className="text-center py-16">
                <h2 className="text-4xl font-bold text-gray-800 mb-4">Ho≈ü Geldiniz</h2>
                <p className="text-lg text-gray-600 max-w-2xl mx-auto">
                  HBS - Hastane Bilgi Sistemi'ne ho≈ü geldiniz.
                  Bu sistem mikroservis mimarisi, event-driven tasarƒ±m ve modern g√ºvenlik yakla≈üƒ±mlarƒ± ile geli≈ütirilmi≈ütir.
                </p>
                <div className="mt-12 grid grid-cols-1 md:grid-cols-3 gap-6 max-w-4xl mx-auto">
                  <div className="bg-white p-6 rounded-lg shadow-md">
                    <Home className="mx-auto mb-4 text-blue-600" size={48} />
                    <h3 className="text-xl font-semibold mb-2">Microservices</h3>
                    <p className="text-gray-600">Baƒüƒ±msƒ±z √∂l√ßeklenebilir servisler</p>
                  </div>
                  <div className="bg-white p-6 rounded-lg shadow-md">
                    <UserPlus className="mx-auto mb-4 text-green-600" size={48} />
                    <h3 className="text-xl font-semibold mb-2">Event-Driven</h3>
                    <p className="text-gray-600">Kafka ile asenkron ileti≈üim</p>
                  </div>
                  <div className="bg-white p-6 rounded-lg shadow-md">
                    <Users className="mx-auto mb-4 text-purple-600" size={48} />
                    <h3 className="text-xl font-semibold mb-2">Zero Trust</h3>
                    <p className="text-gray-600">G√ºvenli ve denetlenebilir</p>
                  </div>
                </div>
              </div>
            } />
            <Route path="/create-patient" element={<CreatePatientForm />} />
            <Route path="/patients" element={<PatientList />} />
          </Routes>
        </main>

        <footer className="bg-white border-t border-gray-200 py-6">
          <div className="container mx-auto px-4 text-center text-gray-600">
            HBS - Hastane Bilgi Sistemi ¬©2026 | Microservices Architecture
          </div>
        </footer>
      </div>
    </Router>
  );
};

export default App;
